import * as THREE from 'three';

class Fire {
    constructor(config) {
        this.flameParticles = []
        // 火焰贴图加载
        const textureLoader = new THREE.TextureLoader()

        var tex = [];
        const fireTexture = textureLoader.load("data:image/png;base64,")

        tex["smoke"] = textureLoader.load(
            "data:image/png;base64,"
        );

        tex["fire"] = textureLoader.load(
            "data:image/png;base64,"
        );
        // 创建火焰粒子
        const flameCount = 100
        for (let i = 0; i < flameCount; i++) {
            const material = new THREE.SpriteMaterial({ map: tex["fire"], transparent: true, opacity: 0.6 })
            const sprite = new THREE.Sprite(material)
            this.resetFlame(sprite)
            scene.add(sprite)
            this.flameParticles.push(sprite)
        }

        this.clock = new THREE.Clock()
    }
    resetFlame(sprite) {
        sprite.position.set(
            (Math.random() - 0.5) * 0.4,
            0,
            (Math.random() - 0.5) * 0.4
        )
        sprite.scale.set(0.5, 0.5, 0.5)
        sprite.userData = {
            velocityY: Math.random() * 0.01 + 0.01,
            life: Math.random() * 2
        }
    }
    updateAnimate() {
        let that = this
        return () => {
            const delta = this.clock.getDelta()
            for (let i = 0; i < that.flameParticles.length; i++) {
                const sprite = that.flameParticles[i]
                sprite.position.y += sprite.userData.velocityY
                sprite.material.opacity -= delta * 0.5
                sprite.userData.life -= delta
                if (sprite.userData.life <= 0 || sprite.material.opacity <= 0) {
                    that.resetFlame(sprite)
                    sprite.material.opacity = 0.6
                }
            }

        }
    }
}

export default Fire